const verifyProfesion = function (profesionId) {
    return new Promise(function (resolve, reject) {
        setTimeout(function () {
            if (profesionId) {
                resolve()
            } else {
                reject(new Error("Oops"));
            }
        }, 2000)
    })
}
module.exports = {
    verifyProfesion: verifyProfesion
}